import Header from "./subcomponnents/header"

const Course = () => {
    const course = [
      {
      id: 1,
        name: 'Half Stack application development',
        parts: [
          {
            name: 'Fundamentals of React',
            exercises: 10
          },
          {
            name: 'Using props to pass data',
            exercises: 7
          },
          {
            name: 'State of a component',
            exercises: 14
          }
        ]
      },
      {
        name: 'Node.js',
        id: 2,
        parts: [
          {
            name: 'Routing',
            exercises: 3,
            id: 1
          },
          {
            name: 'Middlewares',
            exercises: 7,
            id: 2
          }
        ]
      }
    ]
    console.log(course[0].name)
    return ( <>
    {course.map((cours)=>(
      <>
      <Header key={cours.name} course={cours}/>
      <Content key={cours.id} course={cours}/>
      </>
    )
    )}
    {/* <Header course={course[0]}/>
    <Content course={course[0]}/>
    <Content course={course[1]}/> */}

    </>);
}

export default Course;
